import React from "react";
import "./UserRegistration.scss";

import { Formik, Form, Field, ErrorMessage } from "formik";

import * as Yup from "yup";

const initialValues = {
  email: "",
};

const validationScheme = Yup.object().shape({
  email: Yup.string().email("Invalid Email!").required("Required!"),
});

function UserRegistration() {
  return (
    <div>
      <h3 className="text-muted font-weight-bolder">Regstration</h3>
      <hr />

      <h5 className="font-weight-bold text-uppercase">
        {" "}
        Create new account today to reap the benefits of a personalized shopping
        experience.{" "}
      </h5>

      <Formik
        initialValues={initialValues}
        validationSchema={validationScheme}
        onSubmit={(values, actions) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            actions.setSubmitting(false);
          }, 1000);
        }}
      >
        {({ errors, touched }) => (
          <Form>
            <div className="mt-3">
              <label htmlFor="firstName">
                <small>
                  <b>Username or Email Address</b>
                </small>
              </label>
              <Field
                className="form-control rounded-pill"
                type="text"
                id="userName"
                name="userName"
                placeholder="Enter Username or Email Addresss"
              />
              <small style={{ color: "red" }}>
                <ErrorMessage name="userName">
                  {(msg) => <div>{msg}</div>}
                </ErrorMessage>
              </small>
            </div>
          </Form>
        )}
      </Formik>
    </div>
  );
}

export default UserRegistration;
