import React, { useState } from "react";
import "./RightModal.scss";
import {
  MDBContainer,
  MDBBtn,
  MDBModal,
  MDBModalBody,
  MDBModalHeader,
  MDBModalFooter,
} from "mdbreact";
function RightModal(props) {
  const [modal, setmodal] = useState(false);

  const toggle = () => {
    setmodal({
      modal: !modal,
    });
  };

  return (
    <div>
      <MDBContainer>
        <MDBBtn
          color="info"
          onClick={() => {
            toggle();
          }}
        >
          Right
        </MDBBtn>
        <MDBModal
          isOpen={modal}
          toggle={() => {
            toggle();
          }}
          fullHeight
          position="right"
        >
          <MDBModalHeader
            toggle={() => {
              toggle();
            }}
          >
            MDBModal title
          </MDBModalHeader>
          <MDBModalBody>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat.
          </MDBModalBody>
          <MDBModalFooter>
            <MDBBtn
              color="secondary"
              onClick={() => {
                toggle();
              }}
            >
              Close
            </MDBBtn>
            <MDBBtn color="primary">Save changes</MDBBtn>
          </MDBModalFooter>
        </MDBModal>
      </MDBContainer>
    </div>
  );
}

export default RightModal;
