import { ErrorMessage, Field, Form, Formik } from "formik";
import { MDBBtn, MDBCol, MDBRow } from "mdbreact";
import React, { useState } from "react";
import * as Yup from "yup";

const initialValues = {
  firstName: "",
  lastName: "",
  email: "",
  file: "",
};

const validationSchema = Yup.object().shape({
  firstName: Yup.string().required("Required!"),
  lastName: Yup.string().required("Required!"),
  email: Yup.string().email("Email is not valid !").required("Required!"),
});

function AccountDetails() {
  const [fileName, setfileName] = useState("");
  const fileHandleChange = (e) => {
    setfileName(e.target.files[0].name);
  };
  return (
    <div className="border rounded overflow-hidden px-md-5 py-md-4 p-3">
      <h5 className="font-weight-bold">My Address</h5>
      <div className="mt-3">
        <h6 className="text-muted text-uppercase">billing address</h6>
        <hr />
      </div>
      <Formik
        validationSchema={validationSchema}
        initialValues={initialValues}
        // onSubmit={async (values, { setSubmitting }) => {
        //   const res = await fetch(`http://localhost:3000/`, {
        //     method: "POST",

        //     headers: {
        //       "Content-Type": "application/json",
        //       "Access-Control-Allow-Origin": "*",
        //     },
        //   }).then((response) => response.json());
        //   console.log(res);
        //   setSubmitting(false);
        //   props.myCallbackFunction(res);
        // }}

        onSubmit={(values, actions) => {
          setTimeout(() => {
            alert(JSON.stringify(values, null, 2));

            // axios
            //   .post(`http://localhost:5000/`, JSON.stringify(values))
            //   .then((res) => {
            //     console.log(res);
            //     console.log(res.data);
            //   });
            console.log(JSON.stringify(values));

            fetch(`http://localhost:8000/`, {
              method: "POST",
              headers: {
                "Content-Type": "application/json",
                // 'Content-Type': 'application/x-www-form-urlencoded',
              },
              body: JSON.stringify(values),
            })
              .then(() => console.log(e))
              .catch((e) => console.log(e));

            // actions.setSubmitting(true);

            let data = new FormData();
            data.append("file", values.file);
            return fetch("http://localhost:8000/", {
              method: "POST",
              headers: new Headers({ Accept: "application/json" }),
              body: JSON.stringify(data),
            })
              .then((res) => res.json())
              .then((data) => console.log(data))
              .catch((err) => console.log(err));
          }, 1000);
        }}
      >
        {(formProps) => (
          <Form>
            <MDBRow>
              <MDBCol sm="12" md="3">
                <div className="mt-3">
                  <label htmlFor="firstName">
                    <small>
                      <b>First Name</b>
                    </small>
                  </label>
                  <Field
                    className="form-control rounded-pill"
                    type="text"
                    id="firstName"
                    name="firstName"
                    placeholder="Tyumen"
                  />
                  <small style={{ color: "red" }}>
                    <ErrorMessage name="firstName">
                      {(msg) => <div>{msg}</div>}
                    </ErrorMessage>
                  </small>
                </div>
              </MDBCol>
              <MDBCol sm="12" md="3">
                <div className="mt-3">
                  <label htmlFor="lastName">
                    <small>
                      <b>Last Name</b>
                    </small>
                  </label>
                  <Field
                    className="form-control rounded-pill"
                    type="text"
                    id="lastName"
                    name="lastName"
                    placeholder="Tyumen"
                  />
                  <small style={{ color: "red" }}>
                    <ErrorMessage name="lastName">
                      {(msg) => <div>{msg}</div>}
                    </ErrorMessage>
                  </small>
                </div>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <MDBCol md="6" sm="12">
                <div className="mt-3">
                  <label htmlFor="email">
                    <small>
                      <b>E-mail address</b>
                    </small>
                  </label>
                  <Field
                    className="form-control rounded-pill"
                    type="email"
                    id="email"
                    name="email"
                    placeholder="Tyumen"
                  />
                  <small style={{ color: "red" }}>
                    <ErrorMessage name="email">
                      {(msg) => <div>{msg}</div>}
                    </ErrorMessage>
                  </small>
                </div>
              </MDBCol>
            </MDBRow>
            <MDBRow>
              <MDBCol md="6" sm="12">
                <div className="mt-3">
                  <label htmlFor="file">
                    <small>
                      <b>E-mail address</b>
                    </small>
                  </label>

                  <label
                    htmlFor="file"
                    className="w-100  overflow-hidden rounded-pill border d-flex justify-content-end"
                  >
                    <div className="w-75 px-2 py-1"> {fileName} </div>
                    <div className="p-1 w-25 d-flex  justify-content-center align-content-center align-items-center bg-primary">
                      <svg
                        width="26"
                        height="26"
                        viewBox="0 0 26 26"
                        fill="none"
                        xmlns="http://www.w3.org/2000/svg"
                      >
