import React, { useState, useEffect } from "react";
import "./PageNotFound.scss";

import $ from "jquery";

import ImgNumber from "../../assets/404page/404.svg";

function PageNotFound() {
  const [stateX, setstateX] = useState(50);
  const [stateY, setstateY] = useState(50);
  const [elwidth, setelwidth] = useState(0);
  const [elheigth, setelheigth] = useState(0);

  const handleChange = (e) => {
    setstateX(e.nativeEvent.offsetX);
    setstateY(e.nativeEvent.offsetY);
    console.log(stateX);
    console.log(stateY);
  };

  useEffect(() => {
    setelwidth($(".not-found-page").width());
    setelheigth($(".not-found-page").height());
  }, []);

  console.log("this is width" + elwidth);
  console.log("this is height" + elheigth);

  return (
    <div
      className="not-found-page"
      onMouseMove={(e) => {
        handleChange(e);
      }}
    >
      <div className="images-wrap-box">
        <img
          src={ImgNumber}
          className="img-number"
          alt="nimber"
          style={{
            left: `${50 + stateX / 100}%`,
            top: `${50 + stateY / 100}%`,
          }}
        />
      </div>
    </div>
  );
}

export default PageNotFound;
