import React, { useState, useEffect } from "react";
import "./PageNotFound.scss";

import ImgNumber from "../../assets/404page/404.svg";

function PageNotFound() {
  const [stateX, setstateX] = useState(50);
  const [stateY, setstateY] = useState(50);
  const [dimensions, setdimensions] = useState(null);

  const handleChange = (e) => {
    setstateX(e.nativeEvent.offsetX);
    setstateY(e.nativeEvent.offsetY);
    console.log(stateX);
    console.log(stateY);
    console.log("wi" + e.width);
  };

  useEffect(() => {
    setdimensions({
      dimensions: {
        width: this.container.offsetWidth,
        height: this.container.offsetHeight,
      },
    });
  }, []);

  console.log(dimensions.width);
  console.log(dimensions.height);

  return (
    <div
      className="not-found-page"
      onMouseMove={(e) => {
        handleChange(e);
      }}
      ref={(el) => (container = el)}
    >
      <div className="images-wrap-box">
        <img
          src={ImgNumber}
          className="img-number"
          alt="nimber"
          style={{
            left: `${50 + stateX / 100}%`,
            top: `${50 + stateY / 100}%`,
          }}
        />
      </div>
    </div>
  );
}

export default PageNotFound;
