state = {
  name: "",
  email: "",
};

const action = {
  type: "PRODUCT_LIST",
  payload: [
    { id: "1", name: "shapka" },
    { id: "2", name: "shortik" },
  ],
};

export default function (state = {}, action) {
  switch (action) {
    case "PRODUCT_LIST":
      return { ...state, name: action.payload };
    default:
      return state;
  }
}
